function App() {
    const [shavermas, setShavermas] = useState([])

    const removeShaverma = (id) => {
        const newShavermas = shavermas.filter((shaverma) => shaverma.id !== id)

        setShavermas(newShavermas)
    }

    const fetchShavermas = () => {
        setShavermas(data)
    }

    useEffect(() => {
        fetchShavermas()
    }, [])

    return (
        <main>
            <Shavermas shavermas={shavermas} removeShaverma={removeShaverma} />
        </main>
    )
}

export default App
